import UIKit

typealias Base64Image = String

extension Base64Image {
    static let dummy = ""
}

extension Optional where Wrapped == String {
    
    /// Returns nil if the text field was filled with nothing but whitespaces and newlines, the text otherwise.
    var withTrimmedWhitespace: String? {
        guard let text = self?.withTrimmedWhitespace else { return nil }
        return text.count > 0 ? text : nil
    }
    
}

extension String {
    
    /// Removes leading/trailing whitespace and merges consecutive whitespaces into one space.
    var withTrimmedWhitespace: String {
        return self.withCondensedWhitespace.trimmingCharacters(in: .whitespacesAndNewlines)
    }
    
    /// Merges consecutive whitespaces into one space.
    private var withCondensedWhitespace: String {
        let components = self.components(separatedBy: .whitespacesAndNewlines)
        return components.filter { !$0.isEmpty }.joined(separator: " ")
    }
    
}
